import 'package:flutter/material.dart';
import 'package:google_fonts/google_fonts.dart';

class HomeDashboard extends StatelessWidget {
  final String userName;

  const HomeDashboard({super.key, required this.userName});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('MediFlow Dashboard'),
        centerTitle: true,
        backgroundColor: Colors.teal,
      ),
      body: SingleChildScrollView(
        padding: const EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text(
              'Welcome, $userName ðŸ‘‹',
              style: GoogleFonts.poppins(
                fontSize: 22,
                fontWeight: FontWeight.bold,
              ),
            ),
            const SizedBox(height: 20),

            /// Quick Access Buttons
            Wrap(
              spacing: 16,
              runSpacing: 16,
              children: [
                _QuickButton(
                  icon: Icons.chat_bubble_outline,
                  label: 'Talk to AI Assistant',
                  onTap: () {},
                ),
                _QuickButton(
                  icon: Icons.person_outline,
                  label: 'View My Health Profile',
                  onTap: () {},
                ),
                _QuickButton(
                  icon: Icons.history,
                  label: 'View Reports History',
                  onTap: () {},
                ),
                _QuickButton(
                  icon: Icons.local_hospital_outlined,
                  label: 'Contact My Doctor',
                  onTap: () {},
                ),
              ],
            ),

            const SizedBox(height: 30),

            /// Notifications
            Text(
              'Notifications',
              style: GoogleFonts.poppins(fontSize: 18, fontWeight: FontWeight.w600),
            ),
            const SizedBox(height: 10),
            _NotificationTile(
              title: 'Blood Test Report Ready',
              time: '2 hrs ago',
            ),
            _NotificationTile(
              title: 'Time to take your medicine',
              time: 'Today, 9:00 AM',
            ),

            const SizedBox(height: 30),

            /// Quick Health Stats
            Text(
              'Health Summary',
              style: GoogleFonts.poppins(fontSize: 18, fontWeight: FontWeight.w600),
            ),
            const SizedBox(height: 10),
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceAround,
              children: const [
                _HealthStatCard(label: 'Heart Rate', value: '76 bpm'),
                _HealthStatCard(label: 'Blood Pressure', value: '120/80'),
                _HealthStatCard(label: 'O2 Level', value: '98%'),
              ],
            ),
          ],
        ),
      ),
    );
  }
}

/// Quick Button Widget
class _QuickButton extends StatelessWidget {
  final IconData icon;
  final String label;
  final VoidCallback onTap;

  const _QuickButton({required this.icon, required this.label, required this.onTap});

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: onTap,
      child: Container(
        width: 150,
        padding: const EdgeInsets.all(16),
        decoration: BoxDecoration(
          color: Colors.teal.shade100,
          borderRadius: BorderRadius.circular(12),
        ),
        child: Column(
          children: [
            Icon(icon, size: 36, color: Colors.teal.shade700),
            const SizedBox(height: 10),
            Text(
              label,
              textAlign: TextAlign.center,
              style: GoogleFonts.poppins(fontSize: 14),
            ),
          ],
        ),
      ),
    );
  }
}

/// Notification Tile
class _NotificationTile extends StatelessWidget {
  final String title;
  final String time;

  const _NotificationTile({required this.title, required this.time});

  @override
  Widget build(BuildContext context) {
    return Card(
      elevation: 2,
      child: ListTile(
        leading: const Icon(Icons.notifications_active_outlined),
        title: Text(title),
        subtitle: Text(time),
      ),
    );
  }
}

/// Health Stat Card
class _HealthStatCard extends StatelessWidget {
  final String label;
  final String value;

  const _HealthStatCard({required this.label, required this.value});

  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        Text(
          value,
          style: GoogleFonts.poppins(fontSize: 18, fontWeight: FontWeight.bold),
        ),
        Text(
          label,
          style: GoogleFonts.poppins(fontSize: 12),
        ),
      ],
    );
  }
}
